mapper.select:
SELECT SID, ROLEID, SCREATEDATE, SDELETEYN, ACONFIRMYN, ACONFIRMFINALYN, SCONFIRMYN,
       SPASS, SNAME, SPHONE, SLOC, SLOCSHORT, SINTRO, SMODIFYDATE, SOPENINGHOUR,
       SCLOSINGHOUR, SCLOSINGDATE, SDEPOSIT, SMEALFEE, LUNCH, DINNER
    FROM SHOP
    WHERE SCONFIRMYN=#{sconfirm}
      AND ACONFIRMFINALYN=#{aconfirmfinal}

SELECT COUNT(*)
    FROM SHOP
    WHERE SPASS=#{spass}

SELECT COUNT(*)
    FROM SHOP_PHOTO
    WHERE SID=#{sid}

SELECT COUNT(*)
    FROM SHOP
    WHERE SID = #{sid}

SELECT COUNT(*)
    FROM FACILITY
    WHERE SID=#{sid}

mapper.update:
UPDATE FACILITY SET
PARKING=#{parking},
VALET=#{valet},
CORKAGE=#{corkage},
ADULTONLY=#{adultonly},
SOMMELIER=#{sommelier},
LETTERING=#{lettering},
RENTALS=#{rentals}
,PARKINGDESC=#{parkingdesc}
,VALETDESC=#{valetdesc}
,CORKAGEDESC=#{corkagedesc}
,ADULTONLYDESC=#{adultonlydesc}
,SOMMELIERDESC=#{sommelierdesc}
,LETTERINGDESC=#{letteringdesc}
,RENTALSDESC=#{rentalsdesc}
WHERE SID=#{sid}
